mapper.select:
select count(*) from (select b.b_name, b.todonum, b.subject, b.begin, b.end, b.state from (select * from b_manage where id=#{id} and del=0) b, member m where m.id=b.id )
AND b_name like '%'||#{searchUserName}||'%'
AND subject like '%'||#{searchApprTitle}||'%'
AND content like '%'||#{searchApprTitle}||'%'
AND begin >= #{searchStartDate}
AND end <= #{searchEndDate}

select a.*, rownum from (select b.b_name, b.todonum, b.subject, b.begin, b.end, b.state from (select * from b_manage where id=#{id} and del=0) b, member m where m.id=b.id
AND b_name like '%'||#{searchUserName}||'%'
AND subject like '%'||#{searchApprTitle}||'%'
AND content like '%'||#{searchApprTitle}||'%'
AND begin >= #{searchStartDate}
AND end <= #{searchEndDate}
order by todonum desc)a where rownum >= #{start} and rownum <= #{end}

select count(*) from b_manage b, b_manageinfo i, member m
where b.todonum = i.todonum and b.id=m.id
and b.id != #{id}
and i.id = #{id}
and b.del=0

    	AND m.name like '%'||#{searchUserName}||'%'
    
    
    	
    		AND b.subject like '%'||#{searchApprTitle}||'%'
    	
    	
    		AND b.content like '%'||#{searchApprTitle}||'%'
    	
    
    
    	
    		AND b.begin >= #{searchStartDate}
    	
    
    
    	
    		AND b.end <= #{searchEndDate}

select a.*, rownum from (select i.donum, b.todonum, b.b_name, i.id, b.subject, b.begin, b.end, b.state, m.name from(select * from b_manageinfo where id=#{id} and del=0) i, member m, b_manage b where m.id=b.id and b.todonum=i.todonum and b.del=0
AND m.name like '%'||#{searchUserName}||'%'
AND b.subject like '%'||#{searchApprTitle}||'%'
AND b.content like '%'||#{searchApprTitle}||'%'
AND b.begin >= #{searchStartDate}
AND b.end <= #{searchEndDate}
order by todonum desc)a where rownum >= #{start} and rownum <= #{end}

	select count(*) from
        (select b_name, subject, begin, end, state
                from(select * from b_manage where id=#{id} and state=#{state} and del=0))
	
	    	AND b_name like '%'||#{searchUserName}||'%'
	    
	    
	    	
	    		AND subject like '%'||#{searchApprTitle}||'%'
	    	
	    	
	    		AND content like '%'||#{searchApprTitle}||'%'
	    	
	    
	    
	    	
	    		AND begin >= #{searchStartDate}
	    	
	    
	    
	    	
	    		AND end <= #{searchEndDate}

	
		select a.* , rownum from
        (select b_name, subject, begin, end, state
                from(select * from b_manage where id=#{id} and state=#{state} and del=0
		 
		
	    	AND b_name like '%'||#{searchUserName}||'%'
	    
	    
	    	
	    		AND subject like '%'||#{searchApprTitle}||'%'
	    	
	    	
	    		AND content like '%'||#{searchApprTitle}||'%'
	    	
	    
	    
	    	
	    		AND begin >= #{searchStartDate}
	    	
	    
	    
	    	
	    		AND end <= #{searchEndDate}
	    	
	    
	
		order by todonum desc)) a
		where rownum >= #{start} and rownum <= #{end}

select count(*) from b_manage b, member m where b.id=m.id

    	AND m.name like '%'||#{searchUserName}||'%'
    
    
    	
    		AND b.subject like '%'||#{searchApprTitle}||'%'
    	
    	
    		AND b.content like '%'||#{searchApprTitle}||'%'
    	
    
    
    	
    		AND b.begin >= #{searchStartDate}
    	
    
    
    	
    		AND b.end <= #{searchEndDate}


	select a.* , rownum from
	    (select m.name, m.id,b.subject, b.begin, b.end, b.state, b.groupId, b.todonum from
	        b_manage b, member m where b.id=m.id
	 
	
    	AND m.name like '%'||#{searchUserName}||'%'
    
    
    	
    		AND b.subject like '%'||#{searchApprTitle}||'%'
    	
    	
    		AND b.content like '%'||#{searchApprTitle}||'%'
    	
    
    
    	
    		AND b.begin >= #{searchStartDate}
    	
    
    
    	
    		AND b.end <= #{searchEndDate}
    	
    

	order by todonum desc ) a
	where rownum >= #{start} and rownum <= #{end}
